  def indexOfBasement(input: String) = {
    var floor = 0

    def step(c: Char) = if(c == '(') 1 else -1
    def calc(c: Char) = {
      floor += step(c)
      floor
    }
    input.map(calc).indexOf(-1) + 1
  }
  def main(args: Array[String]) {
    println(indexOfBasement(input))
